//获取lw最大值
int get_lw_max(Mat src_gray)
{
	int Lw_max = 0;
	int row = src_gray.rows;
	int col = src_gray.cols;
	int nStep = src_gray.cols * src_gray.channels();

	for (int i = 0; i < row; i++)

	{
		uchar* pSrcData = src_gray.ptr<uchar>(i);

		for (int j = 0; j < nStep; j++)

		{
			int value = pSrcData[j];
			if (value > Lw_max) Lw_max = value;
		}
	}

	return Lw_max;
}

//获取lw对数平均值
//从这里开始数值都经过了定点化，log_value里的值为真实对数值*512，表里共512个元素是为了gain矩阵做准备
int get_lw_average(Mat src_gray)
{
	int sum = 0;
	int Lw_aver = 0;
	int row = src_gray.rows;
	int col = src_gray.cols;
	int nStep = src_gray.cols * src_gray.channels();

	for (int i = 0; i < row; i++)

	{
		uchar* pSrcData = src_gray.ptr<uchar>(i);

		for (int j = 0; j < nStep; j++)

		{
			sum = sum + log_value[pSrcData[j]];
		}
	}
	int coff = sum / (src_gray.rows * src_gray.cols);
	Lw_aver = exp_value[coff + 2838 - 3000];
	//Lw_aver = 3;
	//printf("sum = %ld\n", sum);
	//printf("coff = %d\n", coff);
	//printf("Lw_aver = %d\n", Lw_aver);
	return Lw_aver;
}

//得到亮度提升比矩阵
//gain = log(lw/lw_aver+1)/log(lw_max/lw_aver+1)/lw=(log(lw+lw_aver)-log(lw_aver))/(log(lw_max+lw_aver)-log(lw_aver))/lw
//log值通过查表法得到，gain需要对真实值*65536
Mat get_gain(Mat src_gray, int Lw_aver, int Lw_max)
{
	Mat gain;
	gain = Mat(src_gray.size(), CV_16UC1);
	int row = src_gray.rows;
	int col = src_gray.cols;
	int nStep = src_gray.cols * src_gray.channels();

	for (int i = 0; i < row; i++)

	{
		uchar* pSrcData = src_gray.ptr<uchar>(i);
		short* pResuiltData = gain.ptr<short>(i);
		for (int j = 0; j < nStep; j++)

		{
			int gain_data_1 = 65536 * (log_value[pSrcData[j] + Lw_aver] - log_value[Lw_aver]);
			int gain_data = gain_data_1 / (log_value[Lw_max + Lw_aver] - log_value[Lw_aver]) / (1 + pSrcData[j]);

			pResuiltData[j] = static_cast<short>(gain_data);
			//printf("gain_data_1=%ld", gain_data_1);
			//printf("pResuiltData[j] = %ld ", pResuiltData[j]);
		}
	}
	return gain;
}


//得到gain和原rgb矩阵相乘后的值
Mat get_new_rgb(Mat gain, Mat src)
{
	Mat dst;
	dst = Mat(src.size(), CV_32SC3);
	int row = dst.rows;
	int col = dst.cols;
	int nStep = dst.cols * dst.channels();

	for (int i = 0; i < row; i++)

	{
		uchar* pSrcData_1 = src.ptr<uchar>(i);
		short* pSrcData_2 = gain.ptr<short>(i);
		unsigned int* pResuiltData = dst.ptr<unsigned int>(i);
		for (int j = 0; j < dst.cols * dst.channels(); j += 3)

		{
			pResuiltData[j] = pSrcData_2[j / 3] * pSrcData_1[j];
			pResuiltData[j + 1] = pSrcData_2[j / 3] * pSrcData_1[j + 1];
			pResuiltData[j + 2] = pSrcData_2[j / 3] * pSrcData_1[j + 2];
			//printf("pResuiltData[j] = %ld ", pResuiltData[j]);
		}
	}

	return dst;
}



Mat Ehn_GF_new(Mat src)
{

	//Mat src = imread("C:\Users\bit\Desktop", 1);

	//得到输入亮度lw，即图像灰度值
	Mat src_gray;
	src_gray = Mat(src.size(), CV_8UC1);
	int height = src_gray.rows;
	int width = src_gray.cols;
	int nStep = src.cols * src.channels();
	for (int i = 0; i < height; i++) {
		uchar* src_rows = src.ptr<uchar>(i);
		uchar* gray_rows = src_gray.ptr<uchar>(i);
		for (int j = 0; j < src.cols * src.channels(); j += src.channels()) {
			int b = src_rows[j];
			int g = src_rows[j + 1];
			int r = src_rows[j + 2];
			double gray_data = 0.114 * b + 0.587 * g + 0.299 * r;
			gray_rows[j / src.channels()] = static_cast<uchar>(gray_data);
		}
	}

	//得到输入亮度最大值lw_max和输入亮度对数平均值Lw_aver
	int Lw_max = get_lw_max(src_gray);
	int Lw_aver = get_lw_average(src_gray);

	//得到亮度提升比矩阵gain
	Mat gain = get_gain(src_gray, Lw_aver, Lw_max);
	//得到gain和原rgb矩阵相乘后的值
	Mat dst = get_new_rgb(gain, src);

	//找出新rgb矩阵的最大最小值，准备归一化
	unsigned int min[3] = { 100000, 100000, 100000 };
	unsigned int max[3] = { 0, 0, 0 };

	for (int i = 0; i < height; i++) {
		unsigned int* dst_rows = dst.ptr<unsigned int>(i);
		for (int j = 0; j < dst.cols * dst.channels(); j += dst.channels()) {
			unsigned int b = dst_rows[j];
			unsigned int g = dst_rows[j + 1];
			unsigned int r = dst_rows[j + 2];
			if (b > max[0]) max[0] = b;
			if (b < min[0]) min[0] = b;
			if (g > max[1]) max[1] = g;
			if (g < min[1]) min[1] = g;
			if (r > max[2]) max[2] = r;
			if (r < min[2]) min[2] = r;
		}
	}
	/*printf("Lw_max = %ld\n", max[0]);
	printf("Lw_max = %ld\n", max[1]);
	printf("Lw_max = %ld\n", max[2]);*/

	//对新rgb矩阵归一化到0-255
	Mat dst1, dst_rgb;
	dst_rgb = Mat(src_gray.size(), CV_8UC3);

	for (int i = 0; i < height; i++) {
		unsigned int* dst_rows = dst.ptr<unsigned int>(i);
		uchar* dst_rgb_rows = dst_rgb.ptr<uchar>(i);
		for (int j = 0; j < dst.cols * dst.channels(); j += dst.channels()) {
			dst_rgb_rows[j] = (saturate_cast<uchar>(255 * (dst_rows[j] - min[0]) / (max[0] - min[0])));
			dst_rgb_rows[j + 1] = (saturate_cast<uchar>(255 * (dst_rows[j + 1] - min[1]) / (max[1] - min[1])));
			dst_rgb_rows[j + 2] = (saturate_cast<uchar>(255 * (dst_rows[j + 2] - min[2]) / (max[2] - min[2])));
			//printf("dst_rgb_rows[j+2] = %ld", dst_rgb_rows[j+2]);
		}
	}



	return dst_rgb;
}

